import CabecalhoCadastro from "../componentes/CabecalhoCadastro";
import { Form, Button } from 'react-bootstrap';
import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { alterarLivro, excluirLivro, getLivro, inserirLivro } from "../servico/api"

export default function LivroCad(){
    //Declarando uma variável para utilizar o redirecionamento de tela
    const navigate = useNavigate();

    //Lendo o id recebido na rota
    const { id } = useParams();

    //Variáveis useState para cada campo da tabela, exceto chave primária
    const [autor, setLivro] = useState('');

    // executa quando inicia a pagina
    useEffect(()=>{
        const selecionar = async () => {
            let resposta = await getLivro(id)
            setAutor(resposta.autor)
        }

        if (id){
            selecionar()
        }
    }, [id]);

    const voltar = () => {
        navigate('/livros')
    }

    const cancelar = () => {
        voltar();
    }

    const salvar = async () => {

        if (id){
            //Put - Alterar
            await alterarLivro(id, autor)
        }
        else {
            //Post - Inserir
            await inserirLivro(autor)
        }

        voltar()
    }

    const excluir = async () => {
        if (! window.confirm('Deseja excluir o registro agora?'))
            return;
            await excluirLivro(id)
            voltar()
    }

    return (
        <>
            <CabecalhoCadastro titulo="Livro" id={id}/>

            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Livro</Form.Label>
                    <Form.Control type="text" value={titulo} onChange={e => setLivro(e.target.value)}/>
                    <Form.Control type="int" value={ano} onChange={e => setLivro(e.target.value)}/>
                    <Form.Control type="int" value={paginas} onChange={e => setLivro(e.target.value)}/>
                    <Form.Control type="textarea" value={resumo} onChange={e => setLivro(e.target.value)}/>
                    <Form.Control type="text" value={emprestado} onChange={e => setLivro(e.target.value)}/>
                    <Form.Control type="int" value={idcategoria} onChange={e => setLivro(e.target.value)}/>
                    <Form.Control type="int" value={ideditora} onChange={e => setLivro(e.target.value)}/>
                </Form.Group>

                <Button variant="primary" onClick={()=>salvar()}>Salvar</Button>
                <Button variant="secondary" onClick={()=>cancelar()}>Cancelar</Button>
                <Button variant="danger" onClick={()=>excluir()} hidden={!id}>Excluir</Button>
            </Form>
        </>
    );
}


